nick_name=#{nickName}, email=#{email}, password=#{password}, sex=#{sex}, birthday=#{birthday}, school=#{school}, person_introduction=#{personIntroduction}, join_time=#{joinTime}, last_login_time=#{lastLoginTime}, last_login_ip=#{lastLoginIp}, status=#{status}, notice_info=#{noticeInfo}, total_coin_count=#{totalCoinCount}, current_coin_count=#{currentCoinCount}, theme=#{theme},
where user_id=#{userId}
